let ias=new InfiniteAjaxScroll(".masonry_article",{item:".masonry_item",pagination:".masonry_pager",next:".masonry_next",spinner:{element:".masonry_loader",delay:1e3,show:function(element){element.style.opacity="1"},hide:function(element){element.style.opacity="0"}}});ias.on("last",function(){let el=document.querySelector(".masonry_no-more");el.style.opacity="1"});ias.on("page",e=>{document.title=e.title;let state=history.state;history.replaceState(state,e.title,e.url)});